import React, { useState } from 'react';
import { FaUserAlt, FaMapMarkerAlt, FaComments } from 'react-icons/fa'; // Icons for user feedback

const AboutUs = () => {
  const [userFeedback, setUserFeedback] = useState({
    name: '',
    address: '',
    message: ''
  });
  const [feedbackList, setFeedbackList] = useState([
    { name: 'John Doe', address: 'New York', message: 'Great service! Helps families enjoy content safely.' },
    { name: 'Jane Smith', address: 'Los Angeles', message: 'I love how easy it is to mute or trim content.' }
  ]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserFeedback({ ...userFeedback, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setFeedbackList([...feedbackList, userFeedback]);
    setUserFeedback({ name: '', address: '', message: '' }); // Clear form after submission
  };

  return (
    <div className="bg-black text-white min-h-screen flex flex-col items-center px-4 py-6">
      <header className="w-full py-4">
        <h1 className="text-2xl font-bold text-center">About Us</h1>
      </header>

      <main className="flex flex-col items-center w-full space-y-8">
        <section className="max-w-4xl space-y-6">
          <h2 className="text-2xl font-bold">The Problem: Offensive Content in OTT Platforms</h2>
          <div className="text-xl space-y-4">
            <p>
              Many popular OTT platforms like Netflix, Amazon Prime, Disney+, and others provide a wide range of content.
              However, these platforms often include offensive language such as "f***", "d***", "sh*t", and more. Such language can ruin the viewing experience, especially for families watching together.
            </p>
            <p>
              When parents want to watch a movie or a series with their children, they are often forced to skip certain shows or episodes because of inappropriate language. 
              In fact, a recent survey indicated that **76% of parents** avoid content with explicit language when watching with children.

            </p>
            <img src="data:image/jpeg;base64," 
            alt="Offensive Content" 
            className="w-full rounded-lg shadow-md" />
          </div>

          <h2 className="text-2xl font-bold">The Solution: FamWatch</h2>
          <div className="text-xl space-y-4">
            <p>
              FamWatch is designed to solve this problem by offering the ability to **trim** or **mute** sections of content that contain offensive language. 
              This ensures that families can enjoy the latest shows and movies without worrying about inappropriate language ruining the experience.
            </p>
            <p>
              With FamWatch, users have full control over what they watch, allowing them to create a family-friendly viewing environment for children and adults alike.
            </p>
            <p>
              Whether it's a movie or a series, FamWatch provides a way to tailor content based on individual preferences and make sure the whole family can enjoy it without discomfort.
            </p>
            <img src="data:image/png;base64," 
            alt="FamWatch Solution" 
            className="w-full rounded-lg shadow-md" />
          </div>

          <h2 className="text-xl font-bold">User Feedback</h2>
          <div className="space-y-4">
            <p>
              We’ve received numerous positive reviews from families who appreciate the ability to control the content they watch. Below are a few examples of what users are saying:
            </p>

            {/* Display existing feedback */}
            <div className="space-y-4">
              {feedbackList.map((feedback, index) => (
                <div key={index} className="bg-gray-800 p-4 rounded-lg shadow-md">
                  <p className="flex items-center gap-2">
                    <FaUserAlt className="text-xl" />
                    <strong>{feedback.name}</strong> 
                    <span className="flex items-center gap-1 text-sm text-gray-400">
                      <FaMapMarkerAlt className="text-sm" /> {feedback.address}
                    </span>
                  </p>
                  <p className="mt-2"><FaComments className="inline-block text-lg" /> "{feedback.message}"</p>
                </div>
              ))}
            </div>
          </div>

          <h3 className="text-lg font-semibold">Submit Your Feedback</h3>
          <p>We would love to hear from you! Share your experience with FamWatch and help us improve.</p>

          {/* Feedback Form */}
          <form onSubmit={handleSubmit} className="w-full max-w-md space-y-4 bg-gray-800 p-6 rounded-lg">
            <input
              type="text"
              name="name"
              value={userFeedback.name}
              onChange={handleChange}
              placeholder="Your Name"
              className="w-full p-2 text-black"
              required
            />
            <input
              type="text"
              name="address"
              value={userFeedback.address}
              onChange={handleChange}
              placeholder="Your Address"
              className="w-full p-2 text-black"
              required
            />
            <textarea
              name="message"
              value={userFeedback.message}
              onChange={handleChange}
              placeholder="Your Feedback"
              className="w-full p-2 text-black"
              required
            />
            <button
              type="submit"
              className="btn btn-primary w-full py-2 mt-4"
            >
              Submit Feedback
            </button>
          </form>
        </section>
      </main>
    </div>
  );
};

export default AboutUs;
